defmodule SantaGifts do

    def floor_direction([floor | remain_ins ] , current_floor) when floor === "(" do
        current_floor = current_floor + 1
        floor_direction(remain_ins, current_floor)
    end

    def floor_direction([floor | remain_ins ] , current_floor) when floor === ")" do
        current_floor = current_floor - 1
        floor_direction(remain_ins, current_floor)
    end

    def floor_direction([], current_floor) do
        current_floor
    end

    def init(santa_instruction) do
        santa_instruction |> 
        String.graphemes |>
        floor_direction(0)
    end
end

