defmodule HelpSanta do
    def find_basement_step([inst | rem_inst], current_step, basement_step) when inst === "(" and current_step !== -1 do
        current_step = current_step + 1
        basement_step = basement_step + 1
        find_basement_step(rem_inst, current_step, basement_step)
    end

    def find_basement_step([inst | rem_inst], current_step, basement_step) when inst === ")" and current_step !== -1 do
        current_step = current_step - 1
        basement_step = basement_step + 1
        find_basement_step(rem_inst, current_step, basement_step)
    end

    def find_basement_step([_inst | _rem_inst], current_step, basement_step) when current_step == -1 do
        basement_step
    end

    def find_basement_step([], current_step, basement_step) do
        if current_step === -1 do
            basement_step
        end
    end

    def init(instruction) do
        instruction |>
        String.graphemes |>
        find_basement_step(0, 0)
    end
end

